mapper.select:
        SELECT


--         级联表的字段

        FROM goods  a



                and a.goods_name like CONCAT('%',#{params.goodsName},'%')


                and a.goods_types = #{params.goodsTypes}


                  and a.goods_number >= #{params.goodsNumberStart}   


                  and a.goods_number <= #{params.goodsNumberEnd}   


                and a.danwei like CONCAT('%',#{params.danwei},'%')


                  and a.danjia >= #{params.danjiaStart}   


                  and a.danjia <= #{params.danjiaEnd}   


                and a.goods_content like CONCAT('%',#{params.goodsContent},'%')


                  and a.flag >= #{params.flagStart}   


                  and a.flag <= #{params.flagEnd}   



        order by a.${params.orderBy} desc
