mapper.select:
SELECT
-- 级联表的字段 ,goods.goods_name as goodsName ,goods.goods_types as goodsTypes ,goods.goods_number as goodsNumber ,goods.goods_photo as goodsPhoto ,goods.danwei as danwei ,goods.danjia as danjia ,goods.goods_content as goodsContent ,goods.flag as flag ,inOutOrder.order_name as orderName ,inOutOrder.caozuo_name as caozuoName ,inOutOrder.caozuo_table as caozuoTable ,inOutOrder.order_types as orderTypes ,inOutOrder.insert_time as insertTime
FROM in_out_order_list a left JOIN goods goods ON a.goods_id = goods.id left JOIN in_out_order inOutOrder ON a.in_out_order_id = inOutOrder.id
and a.in_out_order_id = #{params.inOutOrderId}
and a.goods_id = #{params.goodsId}
and a.order_number >= #{params.orderNumberStart}
and a.order_number <= #{params.orderNumberEnd}
and goods.goods_name like CONCAT('%',#{params.goodsName},'%')
and goods.goods_types = #{params.goodsTypes}
and goods.goods_number >= #{params.goodsNumberStart}
and goods.goods_number <= #{params.goodsNumberEnd}
and goods.danwei like CONCAT('%',#{params.danwei},'%')
and goods.danjia >= #{params.danjiaStart}
and goods.danjia <= #{params.danjiaEnd}
and goods.goods_content like CONCAT('%',#{params.goodsContent},'%')
and goods.flag >= #{params.flagStart}
and goods.flag <= #{params.flagEnd}
and inOutOrder.order_name like CONCAT('%',#{params.orderName},'%')
and inOutOrder.caozuo_name like CONCAT('%',#{params.caozuoName},'%')
and inOutOrder.caozuo_table like CONCAT('%',#{params.caozuoTable},'%')
and inOutOrder.order_types = #{params.orderTypes}
and DATE_FORMAT(inOutOrder.insert_time, '%Y-%m-%d %H:%T:%s') >= DATE_FORMAT(#{params.insertTimeStart}, '%Y-%m-%d %H:%T:%s')
and DATE_FORMAT(inOutOrder.insert_time, '%Y-%m-%d %H:%T:%s') <= DATE_FORMAT(#{params.insertTimeEnd} , '%Y-%m-%d %H:%T:%s')
order by a.${params.orderBy} desc
